import { Component, OnInit, Inject, Optional } from '@angular/core';
import { TypeofExpr } from '@angular/compiler';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { UserClickDB } from '../userclick-db'
import { UserClickService } from '../userclick.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-userclick-sorting',
  templateUrl: './userclick-sorting.component.html',
  styleUrls: ['./userclick-sorting.component.css']
})
export class UserClickSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of UserClick instances that are in the association
  associatedUserClicks = new Array<UserClickDB>();

  constructor(
    private userclickService: UserClickService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of userclick instances
    public dialogRef: MatDialogRef<UserClickSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getUserClicks()
  }

  getUserClicks(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let userclick of this.frontRepo.UserClicks_array) {
          let ID = this.dialogData.ID
          let revPointerID = userclick[this.dialogData.ReversePointer as keyof UserClickDB] as unknown as NullInt64
          let revPointerID_Index = userclick[this.dialogData.ReversePointer + "_Index" as keyof UserClickDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedUserClicks.push(userclick)
          }
        }

        // sort associated userclick according to order
        this.associatedUserClicks.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedUserClicks, event.previousIndex, event.currentIndex);

    // set the order of UserClick instances
    let index = 0

    for (let userclick of this.associatedUserClicks) {
      let revPointerID_Index = userclick[this.dialogData.ReversePointer + "_Index" as keyof UserClickDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedUserClicks.forEach(
      userclick => {
        this.userclickService.updateUserClick(userclick)
          .subscribe(userclick => {
            this.userclickService.UserClickServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
}
